function DATA = AddAxisContextMenu(DATA, ax)        if ~isfield(DATA,'CellList')        return;    end    cmenu = uicontextmenu;        cellid = unique(DATA.CellList);    cellid = cellid(cellid>0);    for j = 1:length(cellid)        h = uimenu(cmenu,'label',sprintf('Cell %d',cellid(j)),'Callback',{@AllV.SetCellCompare, cellid(j)});        if DATA.comparecell(cellid(j))            set(h,'checked','on');        end    end    set(ax,'UIContextMenu',cmenu);    