function h = AddCellLabel(DATA,e,p)    [iscell, cellid] = AllV.isacell(DATA, e, p);        xl = get(gca,'xlim');        yl = get(gca,'ylim');    if iscell        cellid = cellid(cellid > 0);        yo=0;        h =text(xl(2),yl(1)+yo,sprintf('%d:Cell %d',p,cellid(1)),'color',DATA.colors{2},'horizontalalignment','right','verticalalignment','bottom','fontsize',DATA.gui.fontsize(1));    else        if ~isfield(DATA,'CellList') || isempty(DATA.CellList)            a = [];        else            [a,b] = find(squeeze(DATA.CellList(:,p,:)) > 0);        end        if length(a)            [c, id] = min(abs(a - e));            cellid = DATA.CellList(a(id),p,b(id));        h = text(xl(2),yl(1),sprintf('%d:(cell %d E%d)',p,cellid,a(id)),'color','r','horizontalalignment','right','verticalalignment','bottom','fontsize',DATA.gui.fontsize(1));        elseif DATA.plot.labelwithid && isfield(DATA.ArrayConfig,'id')            h = text(xl(2),yl(1),sprintf('E%d',DATA.ArrayConfig.id(p)),'color','k','horizontalalignment','right','verticalalignment','bottom','fontsize',DATA.gui.fontsize(1));        else            h = text(xl(2),yl(1),sprintf(' %d',p),'color','k','horizontalalignment','right','verticalalignment','bottom','fontsize',DATA.gui.fontsize(1));        end    end        