function AddComment(a,b,str)        DATA = GetDataFromFig(a);    DATA = AllV.LoadComments(DATA);    n = length(DATA.Comments) + 1;    DATA.Comments(n).user = DATA.user;    DATA.Comments(n).time = now;    DATA.Comments(n).ex = DATA.exptno;    DATA.Comments(n).p = AllV.ProbeNumber(DATA);    DATA.Comments(n).exptno = DATA.exptno;        if length(str)  == 0        x = get(a,'UserData');        if isempty(x)            str = get(a,'string');        else            str = get(x,'string');        end        close(get(a,'parent'));    end    DATA.Comments(n).comment = str;    SaveComments(DATA, DATA.name);    set(DATA.toplevel,'UserData',DATA);        