function AddMarkToPCs(pos, space, plots, varargin)    c = 'g';    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'color',4)            j = j+1;            c = varargin{j};        end        j = j+1;    end   for j = 1:size(plots,1)   [a,xi] = ismember(plots(j,1),space(1,:));   [b,yi] = ismember(plots(j,2),space(1,:));   if a && b       subplot(2,4,j);       hold on;       for k = 1:size(pos,1)           plot(pos(k,xi),pos(k,yi),'+','markersize',10,'linewidth',2,'color',c);           hold on;       end   end               end