function r = CalcClusterDistance(cluster, xy)    cx = cluster.xyr(1);    cy = cluster.xyr(2);    rx = cluster.xyr(3);    ry = cluster.xyr(4);    if isfield(cluster,'aspectratio') & cluster.aspectratio > 0        xys = xyrotate(xy(:,1)-cx,(xy(:,2)-cy) ./cluster.aspectratio,cluster.angle);        r = ((xys(:,1))./rx).^2 + ((xys(:,2))./(ry./cluster.aspectratio)).^2;    else        xys = xyrotate(xy(:,1)-cx,xy(:,2)-cy,cluster.angle);        r = ((xys(:,1))./rx).^2 + ((xys(:,2))./ry).^2;    end    