function DATA = CheckClusterMarks(Clusters, DATA)        if isfield(DATA,'ArrayConfig')        A = DATA.ArrayConfig;    else        return;    end    if isempty(A)        return;    end    A.badexpts(end+1:floor(DATA.exptno)) = 0;    A.badprobes(end+1:DATA.allnprobes) = 0;    nerr = 0;    for j = 1:length(Clusters)        C = Clusters{j};        if isfield(C,'marked') && isfield(C,'exptno')            e = C.exptno;            if C.marked == -1000 & A.badexpts(e) == 0                fprintf('E%dP%d BadExpt in Cluster, not in arrayconfig\n',e,j);                nerr = nerr+1;            end            if C.marked == 3 & A.badprobes(j) == 0                fprintf('E%dP%d BadProbe in Cluster, not in arrayconfig\n',e,j);                nerr = nerr+1;            end        end    end    if nerr && DATA.interactive >= 0        msg = sprintf('%d Bad Marks in Clusters',nerr);        DATA.errpopup = msgbox(msg);        set(DATA.toplevel,'Name',msg);    end  