function ClusterFromPoints(DATA)    if DATA.cluster.shape == 0  %%        G = AllV.GMfit(DATA.xy{1},2,1,'idlist',DATA.clst);        id = cluster(G, DATA.xy{1});        DATA.clst = id;        if isfield(DATA.cluster,'aspectratio')            params = FindEnclosingEllipse(DATA.xy{1},DATA.clst,2,DATA.cluster.aspectratio);        else            params = FindEnclosingEllipse(DATA.xy{1},DATA.clst,2,1);        end        DATA.cluster.xyr = params(1:4);        DATA.cluster.angle = params(5);        DATA = AllV.ClassifyAll(DATA,1);    end