function [distance, cluster] = FindNearestCluster(DATA, pos)    distance = NaN;    cluster = 0;        if DATA.cluster.space(1) == DATA.plottype && sum(DATA.elmousept.pcplot == DATA.cluster.space(2:end)) == length(DATA.elmousept.pcplot)        d(1) = AllV.DistanceToEllipse(DATA.cluster, pos);    else        d(1) = NaN;    end    for j = 1:length(DATA.cluster.next)        if ~isempty(DATA.cluster.next{j}) && DATA.cluster.next{j}.space(1) == DATA.plottype && sum(DATA.elmousept.pcplot == DATA.cluster.next{j}.space(2:end)) == length(DATA.elmousept.pcplot)            d(j+1) = AllV.DistanceToEllipse(DATA.cluster.next{j}, pos);        else            d(j+1) = NaN;        end    end    [distance, cluster] = min(d);    