function FullVKeyPressed(src, ks)if sum(strcmp(ks.Key,{'leftarrow' 'rightarrow'}))     DATA = GetDataFromFig(src);    VT = get(gcf,'UserData');    if strcmp(ks.Key,'leftarrow')        VT.spkid = VT.spkid-1;    elseif strcmp(ks.Key,'rightarrow')        VT.spkid = VT.spkid+1;    end    if VT.spkid > 0 && VT.spkid <= length(VT.ids{2})    t = VT.t(VT.ids{2}(VT.spkid));    set(gca,'xlim',[t-0.002 t + 0.002]);    end    set(gcf,'UserData',VT);    if VT.spkid > length(VT.ids{2})        DATA = get(VT.toplevel,'UserData');        [DATA.currenttrial, DATA.spklst] = AllV.PlotTrialSpikes(DATA, DATA.currenttrial+1);        set(DATA.toplevel,'UserData',DATA);    endelseif sum(strcmp(ks.Key,{'shift'})) end