function need = NeedMore(C, Evec)%Cluster parameters can look bad if ALL of the triggered events are from a%single cell, and tehre are no MU events.     need = 0;if C.auto == 2 %James'    return;end    if nargin > 1        evi = Evec.Eval(1)./sum(Evec.Eval);    else        evi = 0;    end    szratio = std(C.MeanSpike.mu(C.probe,:))./std(C.MeanSpike.ms(C.probe,:));    if C.dropi(3) > 1.7 && C.dropi(4) > 1        need = 1;    elseif C.dropi(3) > 1.9 && C.mahal(1) < 1.5 && szratio > 0.9        need = 3;    elseif C.MeanSpike.muxc > 0.8 && evi > 0.2        need = 2;    else        need = 0;    end            