function OldPlotFullV(V, ispk, DATA)AllV.SetFigure('FullV');AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');hold off;y = 0;for p = 1:length(ispk)plot(V(ispk(p),:)+y);hold on;id = DATA.clid;spts = DATA.spts;for j = 1:length(id);    plot([DATA.t(id(j)):DATA.t(id(j))+length(spts)-1]+spts(1),AllVoltages(ispk(p),:,id(j))+y,'r');endnid = DATA.nid;for j = 1:length(nid);    plot([DATA.t(nid(j)):DATA.t(nid(j))+length(spts)-1]+spts(1),AllVoltages(ispk(p),:,nid(j))+y,'g');endy = y + max(V(ispk(p),:));endif isfield(DATA,'onespiketime')    for j = 1:length(DATA.onespiketime)        plot(DATA.onspiketime,get(gca,'ylim'),'b:');    endendhold off;