function Plot2GaussFit(params, DATA, state)    if length(params) <= 2 %line        C = DATA.cluster;        C.crit = params(1);        xy = DATA.xy{state.cluster};        if length(params) == 2            a = params(2);            xys = xyrotate(xy(:,1),xy(:,2),a-C.angle);        else            xys = xy;        end                r = xys(:,1);    end    [dp, fits] = AllV.Fit2Gauss(C, r, DATA,'plothist')