function PlotAllMeans(DATA)    type = 'lineonly';    Clusters = AllV.mygetappdata(DATA,'Clusters');    [nr,nc] = Nsubplots(length(Clusters));    AllV.SetFigure(DATA.tag.allxy, DATA);    for j = 1:length(Clusters)        mysubplot(nr,nc,j);        hold off;         AllV.PlotMeanSpikes(Clusters{j},0,1,type);        set(gca,'Xtick',[],'Ytick',[]);        h = get(gca,'title');        xl = get(gca,'xlim');        yl = get(gca,'ylim');        a = get(h,'position');        a(2) = yl(2);        a(1) = mean(xl);        set(h,'position',a,'VerticalAlignment','top');        set(gca,'ButtonDownFcn',{@AllV.HitXYPlot, j});        if DATA.selectprobe(j)            set(gca,'xcolor','r','ycolor','r','linewidth',3);        else            set(gca,'xcolor','k','ycolor','k','linewidth',1);        end    end