 function PlotClusterXY(DATA, C)     cid = unique(C.clst);     cid = cid(cid > 0);     if DATA.clplot == 1         id = find(C.clst >0);         [a,D] = DensityPlot(C.xy(id,1),C.xy(id,2),'sd',[2 2],'ynormal');     else     for j = 1:length(cid)         c = cid(j);         id = find(C.clst == c);         plot(C.xy(id,1),C.xy(id,2),'.','markersize',1,'color',DATA.colors{c});         hold on;     end     end     axis('tight');     set(gca,'xtick',[],'ytick',[]);     