function res = PlotClusters(a,b,fcn)[DATA, F] = GetDataFromFig(a);AllV.SetFigure('Clusters', DATA);DataClusters = AllV.mygetappdata(DATA,'Clusters');res = [];if fcn == 1    PlotSpikeTimes(DataClusters);elseif fcn == 2    res = PlotSpikeTimes(DataClusters,'xcorr','matrix');    for j = 1:length(DataClusters)        DataClusters{j}.synci = res.synci(j,:);    end    DATA.xcorrs = res;    set(DATA.toplevel,'UserData',DATA);elseif fcn == 3    PlotSpikeTimes(DataClusters,'probequality');elseif strcmp(fc,'exptxcorr')    res = PlotSpikeTimes(DataClusters,'xcorr');    for j = 1:length(DataClusters)        DataClusters{j}.synci = res.synci(j,:);    end    DATA.xcorrs = res;    set(DATA.toplevel,'UserData',DATA);endDATA = AllV.mysetappdata(DATA,'Clusters',DataClusters);