function xy = PlotOneXY(DATA,names, varargin) xy = [];    if length(names) < 2 || DATA.interactive < 0        return;    end        j = 1;    while j <= length(varargin)        if strcmp(varargin{j},'default')            if DATA.watchplots                AllV.SetFigure(DATA.tag.tmplscore, DATA);            else                return;            end        end               j = j+1;    end        if DATA.xyplot.space(1) == 0        return;    end    dim = [DATA.xyplot.space; DATA.xyplot.id];    [x, xid] = AllV.GetValues(DATA,names{1},dim(:,1));    [y, yid] = AllV.GetValues(DATA,names{2},dim(:,2));    if DATA.xyplot.density        args = {'density'};    else        args = {};    end    if isempty(x)    elseif length(x) == length(y) && length(x) == length(DATA.clst)%        GetFigure(DATA.tag.tmplscore);        hold off;        if DATA.xyplot.density == 1            DensityPlot(x,y,'sd',[2 2],'ynormal');        else           PlotND([x y],[],'idlist',DATA.clst,'colors',DATA.colors,args{:});        end    else        cprintf('red','length mismatch %d vs %d\n',length(x),length(y));    end    if isfield(DATA.cluster,'space') && length(DATA.cluster.space) > 2    if DATA.cluster.space(2) == xid && DATA.cluster.space(3) == yid        DATA.cluster.color = 'r';        AllV.DrawEllipse(DATA.cluster);    end    end    set(gca,'UserData',[xid yid]);    xlabel(names{1});    ylabel(names{2});    xy = [x y];