function PlotVarE(DATA)        if length(DATA.clst) > size(DATA.energy,2) %energy out of date    return;end    colors = 'brgmck';c = DATA.probe(1);if DATA.clplot == 1    hold off;    DensityPlot(DATA.energy(1,DATA.nid),DATA.spkvar(c,:)./DATA.energy(1,:));elsehold off;if DATA.usegmcid && length(DATA.gmcid)    cls = unique(DATA.gmcid);    for j = length(cls)        id = find(DATA.gmcid ==cls(j));        plot(DATA.energy(1,id),DATA.spkvar(c,id)./DATA.energy(1,id),'.','markersize',1,'color',colors(j));    endelseif isfield(DATA,'nid')plot(DATA.energy(1,DATA.nid),DATA.spkvar(c,DATA.nid)./DATA.energy(1,DATA.nid),'.','markersize',1);hold on;plot(DATA.energy(1,DATA.clid),DATA.spkvar(c,DATA.clid)./DATA.energy(1,DATA.clid),'r.','markersize',1);elseplot(DATA.energy(1,DATA.uid),DATA.spkvar(c,DATA.uid)./DATA.energy(1,DATA.uid),'.','markersize',1);endendtitle(sprintf('%d/%d Spikes',length(DATA.clid),DATA.nevents));