function probe = ProbeNumber(DATA)% AllV.ProbeNumber returns Real Probe number% not the index to the current AllVoltages Array.if isfield(DATA,'trueprobe') && DATA.trueprobe > 0    probe = DATA.trueprobe;elseif isfield(DATA,'probelist')        if DATA.probe(1) > length(DATA.probelist)            probe = DATA.probe(1);        elseif DATA.probe(1) == 0            probe = DATA.probelist(1);        else            probe = DATA.probelist(DATA.probe(1));        end    elseif isfield(DATA,'probe')        probe = DATA.probe(1);    else        probe = 1;    end                