function res = QuantifyQuickClusters(DATA, ispk, varargin)         passonargs = {};    j = 1;    while j <= length(varargin)        passonargs = {passonargs{:} varargin{j}};        j = j+1;    end    DataClusters = AllV.mygetappdata(DATA,'Clusters');    for j = 1:length(ispk)            if isfield(DataClusters{ispk(j)},'quick') && DataClusters{ispk(j)}.quick                fprintf('Reclassifying E%d P%d\n',DATA.exptno,ispk(j));                res{j} = AllV.AllVPcs(DATA.toplevel, 'tchannew', ispk(j), passonargs{:},'reclassify');                res{j}.exptno = DATA.exptno;                res{j}.probes = ispk(j);            end    end