 function SetOption(a, b, type)     onoff = {'off' 'on'};     DATA = GetDataFromFig(a);     if nargin < 3         type = get(a,'tag');     end     if strcmp(type,'plain')         DATA.csd = 0;         DATA.dvdy = 0;         DATA.dvdt = 0;         SetMenuCheck(a, 'plain', 1,'exclusive') %         set(a,'Checked',onoff{2)});     else         DATA.(type) = ~DATA.(type);         set(a,'Checked',onoff{DATA.(type)+1});     end     if DATA.dvdy         DATA.csd = 1;     elseif DATA.csd         DATA.csd = 2;     end     if strcmp(type,'usegmcid')         AllV.PlotHistogram(DATA,[]);     elseif sum(strcmp(type,{'recalc' 'csd' 'dvdy' 'dvdt' 'plain'}))         DATA = AllV.SetPCs(DATA, 1,0);     end     set(DATA.toplevel,'UserData',DATA);     