function ShowADCPos(src, data, type)     if type == 0     return; endDATA = GetDataFromFig(src);DATA.ts = now;start = get(gca,'CurrentPoint');    mode = strmatch(get(gcf,'SelectionType'),{'normal' 'alternate'  'extend'  'open'});    x = round(start(1,1));    if type ==3         if  DATA.adcmousept.down &&  ishandle(DATA.adcmousept.h)            delete(DATA.adcmousept.h);            AllV.SetADC(src, data, 10);        end    else    hold on;    if type == 2 && ishandle(DATA.adcmousept.h)  &&  DATA.adcmousept.down == 1        set(DATA.adcmousept.h,'xdata',[x x]);        DATA.adcmousept.x = x;        set(DATA.toplevel,'UserData',DATA);    elseif type == 1        DATA.adcmousept.down = 1;        DATA.adcmousept.h = plot([x x],get(gca,'ylim'),'k:');        DATA.adcmousept.x = x;        set(DATA.toplevel,'UserData',DATA);    end    end        