function ShowFullV(src,b, fcn)   DATA = GetDataFromFig(src);   pos = get(gca,'currentpoint');   x = get(src,'Xdata');   [a,b] = min(abs(x-pos(1,1)));   t(1) = x(b)-0.001;   t(2) = x(b)+0.001;   AllV.SetFigure(DATA.tag.fullv,DATA);   [V,id] = AllV.PlotFullV(DATA,t);   [V,id] = AllV.PlotFullV(DATA,t,'addmean','hold','color','g');   T = AllV.mygetappdata(DATA,'TriggerV');   if ~isempty(T)       plot(V.t(id),T(id));   end   c = find(V.t(id) < x(b));   c = c(end) + DATA.cluster.spts(1)-1;   if DATA.cluster.trigdt == 0       plot(V.t(id([1 end])),[DATA.cluster.Trigger DATA.cluster.Trigger]);   end   if isfield(DATA,'oldSpikes')       [a,b] = min(abs(DATA.oldSpikes.times-x(b)));       hold on;       plot(V.t(id(c+[1:size(DATA.oldSpikes.values,2)])),DATA.oldSpikes.values(b,:),'r');       if DATA.cluster.tsmooth           smv = smooth(DATA.oldSpikes.values(b,:),DATA.cluster.tsmooth,'gauss');           plot(V.t(id(c+[1:size(DATA.oldSpikes.values,2)])),smv,'m');       end       axis('tight');   end   