function SyncSpikes(DATA, cells)    Clusters = AllV.mygetappdata(DATA,'Clusters');P = AllV.Cell2Cluster(cells(1),Clusters);Q = AllV.Cell2Cluster(cells(2),Clusters);    Vall = AllV.mygetappdata(DATA,'Vall');   [a, trigtimes{1}] = ismember(P.times,Vall.t);   [a, trigtimes{2}] = ismember(Q.times,Vall.t);   Spikes{1}.times = P.times'.*10000;   Spikes{2}.times = Q.times'.*10000;    allid = repmat(trigtimes{1},length(DATA.spts),1) + repmat(DATA.spts',1,length(trigtimes{1}));    Spikes{1}.values = reshape(Vall.V(cells(1).p,allid),[size(allid)])';    Spikes{1}.maxv = Vall.intscale(1);    Spikes{1}.maxint = Vall.intscale(2);    allid = repmat(trigtimes{2},length(DATA.spts),1) + repmat(DATA.spts',1,length(trigtimes{2}));    Spikes{2}.values = reshape(Vall.V(cells(2).p,allid),[size(allid)])';    Spikes{2}.maxv = Vall.intscale(1);    Spikes{2}.maxint = Vall.intscale(2);    AllV.SetFigure(DATA.tag.spikes,DATA);    PlotSyncSpikes(DATA,Spikes,[cells.p],[cells.cl]);