function h= oldDrawEllipse(E,varargin)if E.shape == 1    h = AllV.DrawLine(E,varargin{:});    return;enda = (E.pos(3)-E.pos(1))/2; %x radiusb = (E.pos(4)-E.pos(2))/2;sn = 0;cn = 1;x = linspace(0,a);y =  sqrt(b.^2 - (x.*b/a).^2);x = [x fliplr(x) -x fliplr(-x)]+mean(E.pos([1 3]));y = [y fliplr(-y) -y fliplr(y)]+mean(E.pos([2 4]));if ishandle(E.h)     set(E.h,'Xdata',x,'Ydata',y);    h = E.h;else    hold on;    h = plot(real(x),real(y),varargin{:});    hold off;end