function C = GetClusterDef(cluster, n)%return relevant member of teh clsuter struc    %can use GetClusterDef(Clsuters,p.c) where clusters is a cell arry, p.c is%double. or%if iscell(cluster)    Clusters = cluster;    p = floor(n);    cl = round(mod(n,1).*10);    cluster = Clusters{p};    n = cl;end    C = [];    if n == 1        C = cluster;    elseif n-1 <= length(cluster.next)        C = cluster.next{n-1};    else         C = cluster;    end    