function HistButtonDragged(src, data)DATA = get(src,'UserData');if isfield(DATA,'elmousept') && DATA.elmousept.down > 0    if DATA.elmousept.mode == 3        start = get(gca,'CurrentPoint');        DATA.elmousept.pos(3) = start(1,1);        DATA.elmousept.pos(1) = start(1,1);        DATA.elmousept.h = AllV.DrawEllipse(DATA.elmousept, DATA.elmousept.plotargs{:});        if isfield(DATA.elmousept,'h2')            set(DATA.elmousept.h2, 'Xdata', DATA.elmousept.pos([1 3]));        end    else        start = get(gca,'CurrentPoint');        DATA.elmousept.pos(3) = start(1,1);        DATA.elmousept.pos(4) = start(1,2);        DATA.elmousept.h = AllV.DrawEllipse(DATA.elmousept, DATA.elmousept.plotargs{:});    endendset(src,'UserData',DATA);