function c = MarkAxes(ax, mark)    c = 'k';    if mark == 100 %cell        set(gca,'Xcolor','r','Ycolor','r','linewidth',2);        c = 'r';    elseif mark == 2 %good        set(gca,'Xcolor','m','Ycolor','m','linewidth',2);        c = 'm';    elseif mark == 3 %bad        set(gca,'Xcolor','g','Ycolor','g','linewidth',2);        c = 'g';    elseif mark == 4 %good MU        set(gca,'Xcolor','b','Ycolor','b','linewidth',2);        c = 'b';    end    