  function need = NeedClusterData(Cluster, ci)      need = 0;      if ci > 1          C = Cluster.next{ci-1};      else          C = Cluster;      end      % if there is no 'quick' field, should mean this has not been applied      % yet. quick is set to 0 when its been applied. Useful to      % differentiae cuts from command line(1) from interactive changes (2)      if ~isfield(C, 'quick')          need = 1;      elseif C.quick > 0          need = 2;      end      