function PlotFeatures(DATA, a, b, type, id, colors, C, varargin)        ptsz = [1 1];fixrange  = 0;clusterdef = 0;nid = [];j = 1;        nc = unique(id);        nc = nc(nc > 0);        pcs = DATA.spikefeatures;        hold off;        if length(nc) < 8            for j = 1:length(nc)                nid = find(id == nc(j));                if a == 0                plot(pcs(nid,a),pcs(nid,b),'.','markersize',ptsz(2),'color',colors{nc(j)});                else                plot(pcs(nid,a),pcs(nid,b),'.','markersize',ptsz(2),'color',colors{nc(j)});                end                hold on;            end        end