function PlotVals(DATA, a,b, type, id, colors, varargin)ptsz = 1;fixrange  = 0;xstr = [];j = 1;while j <= length(varargin)    if strncmpi(varargin{j},'fixrange',5)        fixrange = 1;    end    j = j+1;endif type(1) == 0    hold off;    xl = get(gca,'xlim');    yl = get(gca,'ylim');    if length(id) == DATA.nevents        nc = unique(id);        nc = nc(nc > 0);        for j = 1:length(nc)            ids{j} = find(id == nc(j));        end    elseif length(id)        ids{2} = id;        ids{1} =setdiff(1:DATA.nevents,id);    else         ids{1} = 1:DATA.nevents;    end    if DATA.plotcsd        AllCSD = AllV.GetCSD(DATA,DATA.plotcsd);        for j = 1:length(ids)            plot(squeeze(AllCSD(a(1),a(2),ids{j})),squeeze(AllCSD(b(1),b(2),ids{j})),'.','markersize',ptsz(1),'color',colors{nc(j)});            hold on;        end    elseif DATA.plottype == 9        for j = 1:length(ids)            plot(squeeze(DATA.energy(a(1),ids{j})),squeeze(DATA.energy(b(1),ids{j})),'.','markersize',ptsz(1),'color',colors{j});            hold on;        end            elseif DATA.plottype == 6        for j = 1:length(ids)            plot(squeeze(DATA.dV(a(1),a(2),ids{j})),squeeze(DATA.dV(b(1),b(2),ids{j})),'.','markersize',ptsz(1),'color',colors{j});            hold on;        end            elseif DATA.plottype == 10        for j = 1:length(ids)            plot(squeeze(DATA.spikefeatures(a(1),ids{j})),squeeze(DATA.spikefeatures(b(1),ids{j})),'.','markersize',ptsz(1),'color',colors{j});            hold on;        end            else        AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');        if DATA.plottype == 8            AllVoltages = diff(AllVoltages,[],2);            xstr = '(dvdt)';        end        for j = 1:length(ids)        plot(squeeze(AllVoltages(a(1),a(2),ids{j})),squeeze(AllVoltages(b(1),b(2),ids{j})),'.','markersize',ptsz(1),'color',colors{j});        hold on;        end    end    axis('tight');    xl = get(gca,'xlim');    yl = get(gca,'ylim');    if fixrange        set(gca,'xlim',xl);        set(gca,'ylim',yl);    end    title(sprintf('%d:%d vs %d:%d%s',a(1),a(2),b(1),b(2),xstr),'position',[mean(xl) yl(2)],'VerticalAlignment','Top');else    AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');        if DATA.plottype == 8            AllVoltages = diff(AllVoltages,[],2);        end    DensityPlot(AllVoltages(a(1),a(2),:),AllVoltages(b(1),b(2),:),'sd',[2 2],'ynormal');end