function ReplotXcorrs(a,b, type)           DATA = GetDataFromFig(a);    xcorrs = getappdata(DATA.toplevel,'xcorrs');    if sum(strcmpi(type, {'meanim' 'xcorrs' 'syncspikes' 'histograms'}))        DATA.plot.xcorrtype = type;        SetMenuCheck(a,'exclusive');    elseif strcmpi(type, 'Shape/Efficacy')        AllV.SetFigure(DATA.tag.xcorrpop,DATA);        ClearPlot;        hold off;    for j = 1:length(xcorrs)    plot(xcorrs(j).shapexc, max(xcorrs(j).efficacy),'o',...        'buttondownfcn',{@AllV.PlotXcorr, xcorrs(j).p(1),xcorrs(j).p(2)});    hold on;    end        set(gca,'yscale','log','ylim',[min([xcorrs.shapexc]) 1]);    xlabel('Shape correlation');    ylabel('Efficacy');    end    