function DATA = RotateCluster(DATA, angle)%just in case its called before cluster is setif isfield(DATA,'elmousept') && isfield(DATA.elmousept,'angle')DATA.elmousept.angle = DATA.elmousept.angle+angle;if isfield(DATA.elmousept,'aspectratio')    ar = DATA.elmousept.aspectratio;else    ar = NaN;end%fprintf('Angle %.2f AR %.3f\n',DATA.elmousept.angle,ar);DATA.elmousept.h = AllV.DrawEllipse(DATA.elmousept, DATA.elmousept.plotargs{:});set(DATA.toplevel,'UserData',DATA);end