function SaveMeanSpikeOnly(DATA, outname)         DataClusters = AllV.mygetappdata(DATA,'Clusters');    if DATA.checkclusters        AllV.CheckClusters(DataClusters,'Save');    end%Saving should only change one probe. So read in fiel from disk, and%only update the current probe, then write out.     if exist(outname,'file')        load(outname);        for j = 1:length(DataClusters);            if j > length(Clusters) || isempty(Clusters{j})                Clusters{j} = DataClusters{j};            elseif ~isfield(Clusters{j},'mahal') %empty really                Clusters{j} = DataClusters{j};            elseif isfield(Clusters{j},'mean') %old                Clusters{j} = rmfield(Clusters{j},'mean');            elseif isfield(Clusters{j},'r')                 Clusters{j} = rmfield(Clusters{j},'r');            elseif isfield(Clusters{j},'clst')                 Clusters{j} = rmfield(Clusters{j},'clst');            end            if ~isfield(Clusters{j},'probe')                Clusters{j}.probe = j;            end            if ~isfield(Clusters{j},'auto')                Clusters{j}.auto = DataClusters{j}.auto;            elseif strfind(outname,'AutoClusterTimes')                Clusters{j}.auto = 1;            end            if ~isfield(Clusters{j},'mahal') %still empty                AutoClusters = getappdata(DATA.toplevel,'AutoClusters');                if length(AutoClusters) >= j                    Clusters{j} = AutoClusters{j};                    fprintf('Cluster %d was empty - reverting to Auto\n',j);                    errordlg(sprintf('Cluster %d was empty Reloaded AutoCluster\n',j),'Cluster Error','modal');                                    else                    errordlg(sprintf('Cluster %d was empty and no AutoCluster\n',j),'Cluster Error','modal');                                    end                                end        end    else        Clusters = DataClusters;    end    Clusters{p}.MeanSpike = DATA.cluster.MeanSpike;    for j = 1:length(DATA.cluster.next)        if isfield(DATA.cluster.next{j},'MeanSpike')            Clusters{p}.next{j}.MeanSpike = DATA.cluster.next{j}.MeanSpike;        end    end      