function idlist = SetTrialList(DATA)    idlist = [];    if ~isfield(DATA.Expt,'Trials')        idlist = 1;        return;    end    tlist = [DATA.Expt.Trials.Trial];        idlist = [DATA.Expt.Trials.id];    id = find(ismember(idlist,DATA.excludetrialids));    if DATA.interactive < 0        return;    end    it = findobj('Tag','ChooseTrial');    if ~isempty(it) & isfield(DATA.Expt,'Trials')        tlist(id) = tlist(id) .* -1;        set(it,'string',sprintf('%d|',tlist),'value',1);    end                