function vpts = SetVsamples(DATA, probe, np, nv)%vpts = SetVsamples(DATA, probe, np, nv) Build arra time/probe pairs for ADC samples%DATA.vsmps = [20 6 15 11 30 20];vsmps = DATA.vsmps;a = probe(1);if length(DATA.chspk > 1)    b = DATA.chspk(1);else    b = a-1;endif length(DATA.chspk > 2)    c = DATA.chspk(end);else    c = a+1;endif length(DATA.chspk) == 1 %only one probe    vpts = [a vsmps(1) a vsmps(2); ...            a vsmps(1) a vsmps(3); ...            a vsmps(1) a vsmps(4); ...            a vsmps(1) 1 vsmps(5); ...            a vsmps(2) a vsmps(3); ...            a vsmps(2) c vsmps(4); ...            a vsmps(2) a vsmps(5); ...            a vsmps(3) b vsmps(4)];else    vpts = [a vsmps(1) a vsmps(2); ...            a vsmps(1) a vsmps(3); ...            a vsmps(1) a vsmps(4); ...            a vsmps(1) b vsmps(5); ...            a vsmps(1) c vsmps(3); ...            a vsmps(1) c vsmps(5); ...            a vsmps(4) a vsmps(5); ...            a vsmps(1) b vsmps(3)];end        id = find(vpts(:,1) < 1);vpts(id,1) = 1;id = find(vpts(:,1) > np);vpts(id,1) = np;id = find(vpts(:,3) < 1);vpts(id,3) = 1;id = find(vpts(:,3) > np);vpts(id,3) = np;id = find(vpts(:,2) > nv);vpts(id,2) = nv;id = find(vpts(:,4) > nv);vpts(id,4) = nv;    