 function DATA = UseAllEvents(DATA)     if min(DATA.clst) < 0  %cluster was removed - need to recalc PCS        recalc = 1;    else        recalc = 0;    end    if length(DATA.clst) < DATA.nevents        clst = DATA.clst;        DATA.clst = ones(DATA.nevents,1);        DATA.clst(DATA.uid(clst)) = clst;    end    DATA.clst = abs(DATA.clst);    DATA.clst(DATA.clst == 0) = 1;    DATA.uid = 1:DATA.nevents;    DATA.excludetrialids = [];    DATA.restricttimerange = [];    DATA.cluster = rmfields(DATA.cluster,'restricttimerange');    DATA.excludetrialids = [];    DATA.hidecluster = 0;    DATA.currentcluster = 1;    if recalc        DATA = AllV.SetPCs(DATA,1,0);    end    AllV.SetTrialList(DATA);    