function [true, cellid] = isacell(DATA, ei, p)% [true, cellid] = AllV.isacell(DATA, ei, p)% true if any clusters on this expt(ei) and probe(p) is in the cell list%cellid is a list of cell numbers for each cluster - will be 0 if a cluster is not a cell     if nargin == 1        ei = DATA.exptno;        p = AllV.ProbeNumber(DATA);    end    cellid = 0;    if ~isfield(DATA,'CellList') || ei > size(DATA.CellList,1)        true = 0;        return;    end    if ~isfield(DATA.CellDetails,'exptids')        fprintf('CellList missing exptids\n');        true = 0;        return;    end    row  = find(DATA.CellDetails.exptids == ei);    true = sum(DATA.CellList(row,p,:),3) > 0;    if true        cellid = squeeze(DATA.CellList(row,p,:));  %      cellid = cellid(cellid>0);    end    