function cmenu = AddCellContextMenu(DATA, type)    cmenu = uicontextmenu;        if strcmp(type,'subplot')    for j = 1:50        uimenu(cmenu,'label',sprintf('Cell %d',j),'Callback',{@PC.SetCellFromSubplot, j});    end    else        uimenu(cmenu,'label',sprintf('Spike%d',j-1),'foregroundcolor',DATA.colors{j});    for k = 1:50        c = uimenu(cmenu,'label',sprintf('Cell %d',k),'Callback',{@PC.SetCellFromLine, j-1,  k});        if k == 1            set(c,'separator','on');        end    end    end    