function handles = AddCellLabels(DATA, eid, probe, varargin)    handles = [];    adjid = [];    yl = get(gca,'Ylim');    xl = get(gca,'Xlim');    braces = 0;    position = [1 0];    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'adjacent',7)            j = j+1;            adjid = varargin{j};        elseif strncmpi(varargin{j},'parenth',7)            braces = 1;        elseif strncmp(varargin{j},'NW',2)            position = [0 1];        end        j = j+1;    end    if position(2) > 0.5        vstep = -diff(yl)/12;    else        vstep = diff(yl)/12;    end            position(1) = xl(1)+ diff(xl) * position(1);    position(2) = yl(1)+ diff(yl) * position(2);    if size(DATA.CellList,1) >= eid        cid = find(DATA.CellList(eid,probe,:) > 0);        nh = 0;        for c = 1:length(cid)            str = sprintf('Cell %d',DATA.CellList(eid,probe,cid(c)));            if braces                str = ['(' str ')'];            end            t = text(position(1),position(2)+vstep*(c-1),str,...                'fontweight','bold','VerticalAlignment','bottom','HorizontalAlignment','right');            if DATA.plot.density                set(t,'color','w');            else                set(t,'color',DATA.colors{cid(c)+1},'ButtonDownFcn',{@PC.ReplotXY, eid, probe, cid(c)});            end            handles(c) = t;            nh = c;        end        aid = find(DATA.CellList(eid,probe,:) == 0 & sum(DATA.CellList(adjid,probe,:),1) > 0);        for c = 1:length(aid)            cellid = DATA.CellList(adjid,probe,aid(c));            cellid = cellid(cellid > 0);            str = sprintf('(E%dCell %d)',adjid(1),cellid(1));            t = text(xl(2),yl(1)+vstep*(nh+c-1),str,...                'fontweight','bold','VerticalAlignment','bottom','HorizontalAlignment','right');            if DATA.plot.density                set(t,'color','w');            else                set(t,'color',DATA.colors{aid(c)+1},'ButtonDownFcn',{@PC.ReplotXY, eid, probe, aid(c)});            end            handles(c+nh) = t;        end        if isfield(DATA,'muCellList')        mid = find(DATA.muCellList(eid,probe,:) > 0);        nh = 0;        for c = 1:length(mid)            str = sprintf('Cell %d MU',DATA.muCellList(eid,probe,mid(c)));            if braces                str = ['(' str ')'];            end            t = text(position(1),position(2)+vstep*(c-1),str,...                'fontweight','bold','VerticalAlignment','bottom','HorizontalAlignment','right');            if DATA.plot.density                set(t,'color','w');            else                set(t,'color',DATA.colors{mid(c)+1},'ButtonDownFcn',{@PC.ReplotXY, eid, probe, mid(c)});            end            handles(c) = t;            nh = c;        end        end    end        