function AddComment(a,b,str)if ishandle(a) && strcmp('AddMenu',str)    F = GetFigure(a);    it = findobj(F,'Tag','CommentMenu','type','uimenu');    if ~isempty(it)        delete(it);    end           hm = uimenu(a,'Label','Comment:','Tag','CommentMenu');    uimenu(hm,'Label','Rate Change is Real','Callback',{@PC.AddComment, 'RateChange: Real'});    uimenu(hm,'Label','Rate Change seems real ?isolation','Callback',{@PC.AddComment, 'RateChange: Seems Real ?Isolation'});    uimenu(hm,'Label','Rate Change seems real ?other','Callback',{@PC.AddComment, 'RateChange: Seems Real ?Other'});    uimenu(hm,'Label','Rate Change is artifact','Callback',{@PC.AddComment, 'RateChange: Artifact'});    uimenu(hm,'Label','Poor Stability','Callback',{@PC.TagMenu, 'poor stability'});    uimenu(hm,'Label','Poor Stability','Callback',{@PC.TagMenu, 'poor stability'});    uimenu(hm,'Label','Poor Isolation','Callback',{@PC.TagMenu, 'poor isolation'});    uimenu(hm,'Label','Dropping Spikes','Callback',{@PC.TagMenu, 'dropping spikes'});    return;end    DATA = GetDataFromFig(a);    DATA = LoadComments(DATA, DATA.name);    n = length(DATA.Comments) + 1;    DATA.Comments(n).user = DATA.user;    DATA.Comments(n).time = now;    DATA.Comments(n).ex = DATA.currentpoint(1);    DATA.Comments(n).p = DATA.currentpoint(2);    DATA.Comments(n).exptno = DATA.exptid(DATA.currentpoint(1));   if length(str)  == 0       x = get(a,'UserData');       if isempty(x)        str = get(a,'string');       else        str = get(x,'string');       end       close(get(a,'parent'));   end    DATA.Comments(n).comment = str;    SaveComments(DATA, DATA.name);    set(DATA.toplevel,'UserData',DATA);    