function DATA = AddKey(DATA, tag, varargin)    if strcmp(tag,DATA.tag.celllist)        DATA = PC.AddSelectedCells(DATA);        imtype = 3;    else        if DATA.usepeaks(DATA.currentpoint(1)) > 0            h = PC.DrawBox(DATA.currentpoint(1),DATA.usepeaks(DATA.currentpoint(1)),1);            set(h,'color','r');        end        DATA.usepeaks(DATA.currentpoint(1)) = DATA.currentpoint(2);        imtype = 1;    end    h = PC.DrawBox(DATA.currentpoint(1),DATA.currentpoint(2),imtype );    set(h,'color','w');    set(DATA.toplevel,'UserData',DATA);