function DATA = AddSelectedCells(DATA)    Clusters = getappdata(DATA.toplevel,'Clusters');    cellid = DATA.currentcell;    DATA.selectprobe(DATA.currentpoint(1),DATA.currentpoint(2)) = 1;    [a,b] = find(DATA.selectprobe);    for j  = 1:length(a)        DATA = PC.SetCellEntry(DATA, Clusters{a(j)}{b(j)}, a(j),b(j),DATA.cellcluster, cellid,'nosave');    end    PC.SaveCellList(DATA);    DATA = PC.MarkCurrentCluster(DATA);    PC.PlotCellList(DATA,'showfig');    set(DATA.toplevel,'UserData',DATA);