function  h = AddTitle(DATA, C, titlemode)    spstr = [];    p = C.probe(1);    if titlemode == 2        if C.space(1) == 6            c = DATA.NDSpaceChars(C.space(2));        else            c = DATA.NDSpaceChars(C.space(1));        end        h=title(sprintf('E%.0fP%s %.2f',C.exptno,PC.ProbeLabel(p,DATA,C.cluster),PC.DistanceMeasure(C, C.cluster,DATA.mahaltype)));    elseif titlemode == 1        h= title(sprintf('E%.0fP%s %.2f  %s %.2f',C.exptno,PC.ProbeLabel(p,DATA),C.mahal(1),spstr,C.mahal(4)));    else        h= title(sprintf('P%s Ex %.0f Gm %.2f  %s (%.2f,%.2f for space %.0f)%.0f',PC.ProbeLabel(p, DATA),C.exptno,C.mahal(1),spstr,C.mahal(4),C.bestspace(1),C.bestspace(2),C.sign));    end