function DATA = ApplyConfig(DATA, varargin)    %    configfile = [];    if isfield(DATA,'configfile')        configfile = DATA.configfile;    end    j = 1;    while j <= length(varargin)        if j == 1 && ischar(varargin{j})            configfile = varargin{j};        end        j = j+1;    end    if isempty(configfile)        return;    end    DATA.configfile = configfile;    DATA = ReadConfig(DATA, DATA.configfile,'print','nochoose');    if isfield(DATA,'layoutfile') && exist(DATA.layoutfile)        PC.ApplyLayout(DATA);    end    set(DATA.toplevel,'UserData',DATA);    