function DATA = ApplyLayout(DATA, varargin)layoutfile = [];if isfield(DATA,'layoutfile')    layoutfile = DATA.layoutfile;endj = 1;while j <= length(varargin)    if j == 1 && ischar(varargin{j})        layoutfile = varargin{j};    end    j = j+1;endif ~exist(layoutfile)        fprintf('Cant read %s\n',layoutfile);        return;endDATA.layoutfile = layoutfile;fprintf('Getting Layout from %s\n',layoutfile);load(DATA.layoutfile);    setappdata(DATA.toplevel,'Figpos',Figpos);    f = fields(Figpos);    for j = 1:length(f)        it = FindFig(f{j});        if length(it) == 1                set(it,'Position',Figpos.(f{j}));        end    end    if exist('Showvals','var')        f = fields(Showvals);        for j = 1:length(f)            DATA.show.(f{j}) = Showvals.(f{j});        end    end    if exist('Datavals','var')        f = fields(Datavals);        for j = 1:length(f)            DATA.(f{j}) = Datavals.(f{j});        end    end