    function CellChanged(DATA)        if DATA.show.watchallcellxy   %hit cell image plot            PC.PlotAllCellXY(DATA);        end        if DATA.show.watchallcellspks            PC.PlotAllCell(DATA,'allspkswithmean');        end        if DATA.show.watchallcellmean            PC.PlotAllCellMean(DATA,'meanlines');        end        it = findobj(DATA.fig.celllist,'Tag','CellNumberId');        if ~isempty(it)            cl = caxis;            nc = size(colormap,1);            cmap = colormap;            ci = round(nc * (get(it,'value')-cl(1))./diff(cl));            if ci <= nc & ci > 0            set(it,'backgroundcolor',cmap(ci,:),'foregroundcolor',1-cmap(ci,:));            end        end