function [err, counts] = CheckExptRateSequence(Expt)    counts = [Expt.Trials.count];    if length(counts) > 100        smw = 10;    elseif length(counts) > 20        smw = 5;    elseif length(counts) == 0        smw = 2;    else        smw = 2;    end    smc = smooth(counts, smw);    err.ff = var(smc)./mean(smc);    b = polyfit(1:length(counts),sqrt(counts),1);    err.mean = mean(counts);    err.slope = length(counts).*b(1)./mean(sqrt(counts));    err.smw = smw;    if isfield(Expt.Header,'exptno')        err.exptno = Expt.Header.exptno;    end    if sum(counts) == 0        fprintf('E%dCell%d no spikes\n',err.exptno,Expt.Header.cellnumber);        err.slope = 0;            end            