function DATA = CheckExpts(DATA, type)    Expts = getappdata(DATA.toplevel,'Expts'); for j = 1:length(Expts)     if isempty(Expts{j})         DATA = PC.AddError(DATA,'Expts{%d} is  empty',j);     elseif strcmp(type,'errs')  &&  isfield(Expts{j}.Header,'errs')         for k = 1:length(Expts{j}.Header.errs)             DATA = PC.AddError(DATA,'%d: %s',j,Expts{j}.Header.errs{k});         end     elseif strcmp(type,'method')         DATA = PC.AddError(DATA,'Ex %d Method %d',j,Expts{j}.Header.ReadMethod);     end end 