function res = CheckExptsRateSequence(Expts)       if iscell(Expts)        for j = 1:length(Expts)            [res.errs(j), counts{j}] = PC.CheckExptRateSequence(Expts{j});            m(j) = mean(counts{j});        end        id = find(m > 0);        mrate = mean(cat(2,counts{id}));        medrate = median(m(m>0));        sd = std(cat(2,counts{id}));        res.vmr = sd./mrate;        for j = 1:length(Expts)            if m(j) > 0;            res.diffs(j) = (res.errs(j).mean)./medrate;            else            res.diffs(j) = 1;            end        end    elseif isstruct(Expts)        res = PC.CheckExptRateSequence(Expts);    end    