function bad = CheckFitDim(C)    bad = 0;   if isfield(C,'gmfit') && isfield(C.gmfit,'mu')            nd = size(C.gmfit.mu,2);       if C.space(1) == 6 && (C.space(2) == 1 && nd ~= 4 || ...                C.space(2) == 4 && nd ~= 6 || ...                C.space(2) == 3&& nd ~= 4)            if C.auto                astr = '(auto)';            else                astr = [];            end                fprintf('E%.0fP%d Space ND%d, but Fit has %d dimensions %s \n',C.exptno,C.probe(1),C.space(2),nd,astr);                bad = 1;       end   elseif ~isfield(C,'space')       cprintf('errors','E%dP%d missing space\n',C.exptno,C.probe(1));   end